//  Return a random number between 0 and a (non-inclusive).
uint8_t RandomRange(uint8_t a){
    // b = $100 % c
    uint8_t b = (uint8_t)(0x100 % a);

// Get a random number
// from 0 to $ff - b.
    uint8_t value;
    do {
        Random();
        value = hram->hRandomAdd;
    } while(value > 0xFF - b);

    return value % a;
}